mapper.select:
SELECT member_pic
  FROM MEMBER
 WHERE MEMBER_ID = #{memberId}

select chat_r_title 
from chat_room
where chat_r_no = #{roomId}

mapper.update:
	 	update chat_user
		set is_online = 'true'
		where member_pk = #{memberPk} 
     	and  chat_r_no = #{chatRNo}

	 	update chat_user
		set is_online = 'false'
		where member_pk = #{memberPk} 
     	and  chat_r_no = #{chatRNo}

 	update chat_user
	set is_online = 'false'
	where member_pk = #{memberPk}

mapper.select:
	 select member_pk, chat_r_no from chat_user 
     where chat_r_no = #{roomId}
		 and is_online = 'false'

 	select count(*) from chat_no_read
	where member_pk = #{memberId}
	and chat_r_no = #{roomId}

	 select *
		from(
   			 select *
  			 from chat_content
   			 where chat_c_no = #{roomId}
   			 order by chat_date DESC
			)
			where ROWNUM = 1

select * from chat_user
where chat_r_no = #{roomId}
order by member_pk desc

mapper.insert:
INSERT INTO BFILE (
