mapper.insert:

    SELECT SEQ_SUBWORK.NEXTVAL FROM DUAL


INSERT INTO SUBWORK (
    SUBWORK_NO
  , TOPWORK_NO
  , TOPWORK_R_NO
  , SUBWORK_TITLE
  , SUBWORK_CONTENT
  , SUBWORK_START
  , SUBWORK_END
  , SUBWORK_STATE
  , SUBWORK_PROGRESS
  , SUBWORK_LIKE
  , SUBWORK_FILE
) VALUES (
    #{subWorkNo}
  , #{topWorkNo}
  , #{topWorkRNo}
  , #{subWorkTitle}
  , #{subWorkContent}
  , #{subWorkStart}
  , #{subWorkEnd}
  , 0
  , 0
  , 0
  , 0
)

mapper.update:
UPDATE SUBWORK
   SET SUBWORK_TITLE = #{subWorkTitle}
     , SUBWORK_CONTENT = #{subWorkContent}
     , SUBWORK_START = #{subWorkStart}
     , SUBWORK_END = #{subWorkEnd}
     , SUBWORK_STATE = #{subWorkState}
     , SUBWORK_PROGRESS = #{subWorkProgress}
 WHERE SUBWORK_NO = #{subWorkNo}

