   SELECT SEQ_SW_COMENT.NEXTVAL FROM DUAL

INSERT INTO SUBWORK_COMENT (
    SW_COMENT_NO
  , SUBWORK_NO
  , MEMBER_ID
  , SW_COMENT_DATE
  , SW_COMENT_CONTENT
  , SW_COMENT_FILE
  , SW_COMENT_STATE
) VALUES (
    #{swComentNo}
  , #{subWorkNo}
  , #{memberId}
  , SYSDATE
  , #{swComentContent}
  , '0'
  , '0'
)

mapper.update:
UPDATE SUBWORK_COMENT
   SET SW_COMENT_STATE = '1'
 WHERE SW_COMENT_NO = #{swComentNo}

UPDATE SUBWORK_COMENT
   SET SW_COMENT_CONTENT = #{swComentContent}
 WHERE SW_COMENT_NO = #{swComentNo}

